/* ********************** */
/* Helper Functions       */
/* ********************** */
function GenerateDateString(isoDate) {
  const date = new Date(isoDate);
  var strArray = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  var d = date.getDate();
  var m = strArray[date.getMonth()];
  var y = date.getFullYear();
  return "" + (d <= 9 ? "0" + d : d) + ",  " + m + " " + y;
}

/* ********************** */
/* Pagination             */
/* ********************** */

let hidePageChangeButtons = false;
function togglePageChangeButtons() {
  if (hidePageChangeButtons) {
    pageNumbers.forEach((el) => el.classList.add("invisible"));
  } else {
    pageNumbers.forEach((el) => el.classList.remove("invisible"));
  }
}

let mobile = false;
if (window.innerWidth < 500) {
  mobile = true;
}

const getGridColumnCount = function () {
  const gridComputedStyle = window.getComputedStyle(cardsContainer);

  return gridComputedStyle.getPropertyValue("grid-template-columns").split(" ")
    .length;
};

let page = 0;

const allProjectsTop = document.getElementById("all-projects");
const displayProjectsOnPages = function (data) {
  const columns = getGridColumnCount();

  let perChunk;
  if (mobile) {
    perChunk = 10;
  } else {
    perChunk = columns * 4;
  }

  const result = data.reduce((resultArray, item, index) => {
    const chunkIndex = Math.floor(index / perChunk);

    if (!resultArray[chunkIndex]) {
      resultArray[chunkIndex] = []; // start a new chunk
    }

    resultArray[chunkIndex].push(item);

    return resultArray;
  }, []);
  pageNumberTotal.innerText = result.length;
  if (pageNumberTotalMobile) {
    pageNumberTotalMobile.innerText = result.length;
  }

  console.log(result[page]);
  if (result[page]) {
    displayProjects(result[page]);
    pageNumberCurrent.innerText = page + 1;

    if (pageNumberCurrentMobile) {
      pageNumberCurrentMobile.innerText = page + 1;
    }
  } else {
    page = result.length - 1;
  }
};

